import { useNavigate } from 'react-router-dom';
import Page from '../UI/Page';
import { useState } from 'react';

import classes from './EventForm.module.css';

function EventForm({ method, event, onEvent}) {
  const [title, setTitle] = useState();
  const [image, setImage] = useState();
  const [date, setDate] = useState();
  const [description, setDescription] = useState();


  const navigate = useNavigate();
  function cancelHandler() {
    navigate('..');
  }
  function inputHandler(key, value){
    if(key === 'title'){
      setTitle(value);

    }
    else if(key === 'image'){
        setImage(value);
    }
    else if(key === date){
          setDate(value);
    }
    else{
      setDescription(value);
    }

  }
  function submitEventHandler(event){
     event.preventDefault();
     const newEvent={
      title: title,
      image: image,
      date: date,
      description, description
     }
     if(typeof onEvent ==='function'){
      onEvent(newEvent);
     }
     setDate();
     setDescription();
     setImage()
     setTitle()
  }

  return (
    <Page className={classes.data}>
    <form className={classes.form} onSubmit={submitEventHandler}>
      <p>
        <label htmlFor="title">Title</label>
        <input id="title" type="text" name="title" required 
        defaultValue={event?event.title:""} onChange={(event)=>inputHandler('title',event.target.value)}
        />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input id="image" type="url" name="image" required defaultValue={event?event.image:""} 
        onChange={(event)=>inputHandler('image',event.target.value)}
        />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input id="date" type="date" name="date" required defaultValue={event?event.date:""}
        onChange={(event)=>inputHandler('date',event.target.value)}
        />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea id="description" name="description" rows="5" required defaultValue={event?event.description:""}
         onChange={(event)=>inputHandler('description',event.target.value)}
        />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler}>  
          Cancel
        </button>
        <button type='submit'>Save</button>
      </div>
    </form>
    </Page>
  );
}

export default EventForm;
